CREATE TABLE `notification_staff_reads` (  `client_id` int(11) DEFAULT NULL COMMENT 'クライアントID',  `staff_id` int(11) NOT NULL COMMENT 'スタッフID',  `staff_type` varchar(4) COLLATE utf8_unicode_ci NOT NULL COMMENT 'スタッフ種別[AA,TA,CE]',  `already_read_notification_id_list` LONGTEXT COLLATE utf8_unicode_ci NOT NULL COMMENT '既読したお知らせidリスト',  `create_date` datetime NOT NULL COMMENT '登録日',  `update_date` datetime NOT NULL COMMENT '更新日',  KEY `idx_notification_staff_read` (`client_id`,`staff_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;